# Python Librariesimport urllibimport requestsfrom datetime import datetime# Local modulesfrom .config import user, pswd# Format access pointURL = 'https://st-dev-data-api.azurewebsites.net'# url = urllib.parse.urljoin(URL, '/api/v0.1/buildings/energyville1/transfo/realtime/properties')url = urllib.parse.urljoin(URL, '/api/v0.1/buildings/energyville1/transfo/realtime')# Format parametersDATE_FORMAT_STR = "%Y-%m-%d"start = datetime(2021, 11, 20)end = datetime(2021, 11, 21)params = {    # SmarThor parameters    'start': start.strftime(DATE_FORMAT_STR),    'end': end.strftime(DATE_FORMAT_STR),    'time_zone': 'Central European Standard Time',    # Endpoint specific parameters    'transfo_id': 'transfo1',    'properties': 'ActiveImport'}if __name__ == "__main__":    # Send request    result = requests.get(url, params=params, auth=(user, pswd))    # Visualise / Save response    print(result.status_code)    print(result.text)